import React, { useState, useEffect } from 'react';
import './Intro.css';

const Intro = () => {
  const [highlightedKeywordIndex, setHighlightedKeywordIndex] = useState(0);

  const keywords = [
    { word: "Beijing", title: "Beijing", description: "My journey began in Beijing, a city rich in culture and history.", img: process.env.PUBLIC_URL + '/logo512.png' },
    { word: "UWMadison", title: "UW-Madison", description: "I continued my academic pursuit at UW-Madison, focusing on Computer Science and Mathematics.", img: process.env.PUBLIC_URL + '/logo512.png' },
    { word: "CS&Math", title: "CS & Math", description: "Delving into the realms of Computer Science and Mathematics has been a fascinating journey.", img: process.env.PUBLIC_URL + '/cs-math.png' }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setHighlightedKeywordIndex(prevIndex => (prevIndex + 1) % keywords.length);
    }, 3000); // Change highlighted keyword every 3 seconds

    return () => clearInterval(interval);
  }, [keywords.length]);

  return (
    <div className="intro">
      <div className="intro__card">
        {highlightedKeywordIndex !== -1 && (
          <>
            <img src={keywords[highlightedKeywordIndex].img} alt={keywords[highlightedKeywordIndex].title} className="intro__image"/>
            <h3>{keywords[highlightedKeywordIndex].title}</h3>
            <p>{keywords[highlightedKeywordIndex].description}</p>
          </>
        )}
      </div>
      <div className="intro__text">
        <h2>About Me</h2>
        <p>
          My journey began in <span className={`keyword ${highlightedKeywordIndex === 0 ? "highlighted" : ""}`}>Beijing</span>,
          continued at <span className={`keyword ${highlightedKeywordIndex === 1 ? "highlighted" : ""}`}>UW-Madison</span>,
          and is currently focused on <span className={`keyword ${highlightedKeywordIndex === 2 ? "highlighted" : ""}`}>CS & Math</span>.
        </p>
      </div>
    </div>
  );
};

export default Intro;
