import React, { useState } from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Intro.css';
import { about } from '../../portfolio'; // 假设about对象包含了照片和介绍的数组

const Intro = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const { photos } = about; // 假设photos是一个包含{img: string, description: string}对象的数组

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    beforeChange: (current, next) => setCurrentSlide(next)
  };

  return (
    <div className="intro">
      <div className="intro__card">
        <img src={photos[currentSlide].img} alt="Display" />
        <div className="intro__description">
          <p>{photos[currentSlide].description}</p>
        </div>
      </div>
      <div className="intro__slider">
        <Slider {...settings}>
          {photos.map((photo, index) => (
            <div key={index}>
              <h3>{photo.title}</h3> {/* 假设每个photo对象还有一个title属性 */}
            </div>
          ))}
        </Slider>
      </div>
    </div>
  );
};

export default Intro;
