import React, { useState, useEffect } from 'react';
import './Intro.css';

const Intro = () => {
  const [currentKeyword, setCurrentKeyword] = useState(null);
  const [showProgressBar, setShowProgressBar] = useState(false);

  const keywords = [
    { word: "Beijing", title: "Beijing", description: "I started my college journey in Beijing in 2021, embracing the vibrant life and culture of this magnificent city.", img: process.env.PUBLIC_URL + '/beijing.png' },
    { word: "UWMadison", title: "UW-Madison", description: "Currently, I'm pursuing my studies at UW-Madison, focusing on Mathematics and Computer Science. The university's commitment to excellence and innovation inspires me every day.", img: process.env.PUBLIC_URL + '/uw-madison.png' },
    { word: "Programming", title: "Programming", description: "Ever since I was young, programming has been something that I am passionate about. I have been trying to code various applications and platforms, including web development, application development, and data analysis.", img: process.env.PUBLIC_URL + '/programming.png' },
    { word: "Impact", title: "Making an Impact", description: "I aspire to utilize my skills as a Software Developer to create applications that can help people in need, making a tangible impact on society.", img: process.env.PUBLIC_URL + '/impact.png' }
  ];

  const handleKeywordHover = (keyword) => {
    setCurrentKeyword(keyword);
  };

  useEffect(() => {
    setShowProgressBar(true);
    const timer = setTimeout(() => {
      setShowProgressBar(false);
    }, 3000); // Adjust based on text length
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="intro">
      <div className="intro__card">
        {currentKeyword ? (
          <>
            <img src={currentKeyword.img} alt={currentKeyword.title} className="intro__image"/>
            <h3>{currentKeyword.title}</h3>
            <p>{currentKeyword.description}</p>
          </>
        ) : (
          <p>Hover over the keywords to see more details.</p>
        )}
      </div>
      <div className="intro__text">
        <h2>About Me</h2>
        <p>
          My journey began in <span onMouseOver={() => handleKeywordHover(keywords[0])}>Beijing<div className="keyword-node"></div></span>,
          and in 2021, I took a significant step by joining <span onMouseOver={() => handleKeywordHover(keywords[1])}>UW-Madison<div className="keyword-node"></div></span>.
          Here, I delve into the realms of <span onMouseOver={() => handleKeywordHover(keywords[1])}>Mathematics and Computer Science<div className="keyword-node"></div></span>,
          pursuing my passion and aiming for innovation.
        </p>
        <p>
          <span onMouseOver={() => handleKeywordHover(keywords[2])}>Programming<div className="keyword-node"></div></span> has always been my passion.
          I am currently studying <span onMouseOver={() => handleKeywordHover(keywords[1])}>Computer Science & Data Science<div className="keyword-node"></div></span> at the University of Wisconsin-Madison,
          where I am inspired daily by the commitment to excellence and innovation.
        </p>
        <p>
          I have been coding various applications and platforms, including <span onMouseOver={() => handleKeywordHover(keywords[2])}>web development, application development, and data analysis<div className="keyword-node"></div></span>.
          My goal is to <span onMouseOver={() => handleKeywordHover(keywords[3])}>make an impact<div className="keyword-node"></div></span> by creating applications that help people in need.
        </p>
        {showProgressBar && <div className="intro__progress-bar"></div>}
      </div>
    </div>
  );
};

export default Intro;
