import React, { useState, useEffect } from 'react';
import './Intro.css';

const Intro = () => {
  const [highlightedKeywordIndex, setHighlightedKeywordIndex] = useState(-1);

  const keywords = [
    { word: "Beijing", title: "Beijing", description: "I started my college journey in Beijing in 2021, embracing the vibrant life and culture of this magnificent city.", img: process.env.PUBLIC_URL + '/beijing.png' },
    { word: "UWMadison", title: "UW-Madison", description: "Currently, I'm pursuing my studies at UW-Madison, focusing on Mathematics and Computer Science. The university's commitment to excellence and innovation inspires me every day.", img: process.env.PUBLIC_URL + '/uw-madison.png' },
    { word: "Programming", title: "Programming", description: "Ever since I was young, programming has been something that I am passionate about. I have been trying to code various applications and platforms, including web development, application development, and data analysis.", img: process.env.PUBLIC_URL + '/programming.png' },
    { word: "Impact", title: "Making an Impact", description: "I aspire to utilize my skills as a Software Developer to create applications that can help people in need, making a tangible impact on society.", img: process.env.PUBLIC_URL + '/impact.png' }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setHighlightedKeywordIndex(prevIndex => (prevIndex + 1) % keywords.length);
    }, 3000); // Change keyword every 3 seconds

    return () => clearInterval(interval);
  }, [keywords.length]);

  return (
    <div className="intro">
      <div className="intro__card">
        {highlightedKeywordIndex !== -1 && (
          <>
            <img src={keywords[highlightedKeywordIndex].img} alt={keywords[highlightedKeywordIndex].title} className="intro__image"/>
            <h3>{keywords[highlightedKeywordIndex].title}</h3>
            <p>{keywords[highlightedKeywordIndex].description}</p>
          </>
        )}
      </div>
      <div className="intro__text">
        <h2>About Me</h2>
        <p>
          My journey began in <span className={`keyword ${highlightedKeywordIndex === 0 ? "highlighted" : ""}`}>Beijing</span>,
          and in 2021, I took a significant step by joining <span className={`keyword ${highlightedKeywordIndex === 1 ? "highlighted" : ""}`}>UW-Madison</span>.
          Here, I delve into the realms of <span className={`keyword ${highlightedKeywordIndex === 2 ? "highlighted" : ""}`}>Mathematics and Computer Science</span>,
          pursuing my passion and aiming for innovation.
        </p>
        <p>
          <span className={`keyword ${highlightedKeywordIndex === 2 ? "highlighted" : ""}`}>Programming</span> has always been my passion.
          I am currently studying <span className={`keyword ${highlightedKeywordIndex === 1 ? "highlighted" : ""}`}>Computer Science & Data Science</span> at the University of Wisconsin-Madison,
          where I am inspired daily by the commitment to excellence and innovation.
        </p>
        <p>
          I have been coding various applications and platforms, including <span className={`keyword ${highlightedKeywordIndex === 2 ? "highlighted" : ""}`}>web development, application development, and data analysis</span>.
          My goal is to <span className={`keyword ${highlightedKeywordIndex === 3 ? "highlighted" : ""}`}>make an impact</span> by creating applications that help people in need.
        </p>
      </div>
    </div>
  );
};

export default Intro;
