import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Intro.css';
import mePhoto from './Access/mePhoto2.jpg'; // 确保这个路径是正确的

const Intro = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <div className="intro">
      <div className="intro__image-container">
        {/* 直接显示图片，不需要轮播 */}
        <img src={mePhoto} alt="Me" />
      </div>
      <div className="intro__content">
        {/* 右边是一个滑轮，用于展示不同的介绍内容 */}
        <Slider {...settings}>
          <div>
            <h2>About Me</h2>
            <p>I am currently studying computer science & data science at the University of Wisconsin–Madison.</p>
          </div>
          {/* 添加更多的介绍内容作为不同的slides */}
          {/* ... */}
        </Slider>
      </div>
    </div>
  );
};

export default Intro;
