import React, { useState, useEffect } from 'react';
import './Intro.css';

const Intro = () => {
  const [currentKeyword, setCurrentKeyword] = useState(null);
  const [showProgressBar, setShowProgressBar] = useState(false);

  const keywords = [
    { word: "Beijing", title: "北京", description: "我生活在北京，这是一个美丽的城市。", img: process.env.PUBLIC_URL + '/logo512.png' },
    { word: "UWMadison", title: "UW-Madison", description: "我在UW-Madison学习计算机科学。", img: process.env.PUBLIC_URL + '/logo512.png' },
    { word: "CS", title: "计算机科学", description: "计算机科学是我的专业，我对它充满热情。", img: process.env.PUBLIC_URL + '/logo512.png' }
  ];

  const handleKeywordHover = (keyword) => {
    setCurrentKeyword(keyword);
  };

  useEffect(() => {
    setShowProgressBar(true);
    // 假设每个字的打印时间为100ms
    const timer = setTimeout(() => {
      setShowProgressBar(false);
    }, 3000); // 这里的3000应该根据实际文本长度调整
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="intro">
      <div className="intro__card">
        {currentKeyword ? (
          <>
            <img src={currentKeyword.img} alt={currentKeyword.title} className="intro__image"/>
            <h3>{currentKeyword.title}</h3>
            <p>{currentKeyword.description}</p>
          </>
        ) : (
          <p>请将指示器移动到关键词上查看详情。</p>
        )}
      </div>
      <div className="intro__text">
        <h2>About me</h2>
        <p>
          我生活在<span onMouseOver={() => handleKeywordHover(keywords[0])}>Beijing<div className="keyword-node"></div></span>，
          学习在<span onMouseOver={() => handleKeywordHover(keywords[1])}>UWMadison<div className="keyword-node"></div></span>，
          学习计算机<span onMouseOver={() => handleKeywordHover(keywords[2])}>CS<div className="keyword-node"></div></span>。
        </p>
        {showProgressBar && <div className="intro__progress-bar"></div>}
      </div>
    </div>
  );
};

export default Intro;
