import React, { useState } from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Intro.css';
// 假设about对象是直接导入的
import { about } from '../../portfolio';

const Intro = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  // 确保photos数组存在，否则使用空数组作为默认值
  const photos = about.photos || [];

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    beforeChange: (current, next) => setCurrentSlide(next),
  };

  // 如果photos数组为空，可以选择渲染备用内容或返回null
  if (photos.length === 0) {
    return <div>Loading...</div>; // 或者其他备用内容
  }

  return (
    <div className="intro">
      <div className="intro__card">
        <img src={photos[currentSlide].img} alt="Display" />
        <div className="intro__description">
          <p>{photos[currentSlide].description}</p>
        </div>
      </div>
      <div className="intro__slider">
        <Slider {...settings}>
          {photos.map((photo, index) => (
            <div key={index}>
              <h3>{photo.title}</h3> {/* 假设每个photo对象还有一个title属性 */}
            </div>
          ))}
        </Slider>
      </div>
    </div>
  );
};

export default Intro;
