import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Intro.css';
import { about } from '../../portfolio'; // 假设about对象包含了照片路径

const Intro = () => {
  const { photo } = about; // 从about对象中获取照片路径
  const settings = {
    dots: true,
    infinite: true,
    vertical: true, // 垂直滑动
    verticalSwiping: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <div className="intro">
      <div className="intro__image-container">
        <img src={photo} alt="Me" />
        <div className="intro__description">
          {/* 这里放置你的个人介绍 */}
          <h2>About Me</h2>
          <p>I am currently studying computer science & data science at the University of Wisconsin–Madison.</p>
        </div>
      </div>
      <div className="intro__content">
        <Slider {...settings}>
          {/* 这里放置你的滑轮内容 */}
          <div>
            <h3>My Interests</h3>
            <p>Detail about your interests...</p>
          </div>
          <div>
            <h3>My Projects</h3>
            <p>Detail about your projects...</p>
          </div>
          {/* 更多内容 */}
        </Slider>
      </div>
    </div>
  );
};

export default Intro;
