import React, { useState } from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Intro.css';

const Intro = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  // 在组件内部定义photos数组，包含照片路径和介绍
  const photos = [
    {
      img: process.env.PUBLIC_URL + '/logo512.png',
      description: '这是我的第一个项目，一个非常有趣的Web应用，它可以帮助人们在线学习编程。',
      title: '项目一'
    },
    {
      img: process.env.PUBLIC_URL + '/logo512.png',
      description: '我的第二个项目是一个移动应用，它旨在帮助用户更好地管理他们的日常任务和提醒。',
      title: '项目二'
    },
    {
      img: process.env.PUBLIC_URL + '/logo512.png',
      description: '最后，我的第三个项目是一个数据分析平台，它可以对大量数据进行可视化展示。',
      title: '项目三'
    }
  ];

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    beforeChange: (current, next) => setCurrentSlide(next),
  };

  return (
    <div className="intro">
      <div className="intro__card">
        <img src={photos[currentSlide].img} alt="Display" className="intro__image"/>
        <div className="intro__description">
          <p>{photos[currentSlide].description}</p>
        </div>
      </div>
      <div className="intro__slider">
        <Slider {...settings}>
          {photos.map((_, index) => (
            <div key={index}>
              <h3>{photos[index].title}</h3>
            </div>
          ))}
        </Slider>
      </div>
    </div>
  );
};

export default Intro;
