import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Intro.css';

const Intro = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  return (
    <div className="intro">
      <div className="intro__image-container">
        <Slider {...settings}>
          {/* 在这里放置你的图片，每张图片作为一个slide */}
          <div>
            <img src="path-to-your-image-1.jpg" alt="Intro 1" />
          </div>
          <div>
            <img src="path-to-your-image-2.jpg" alt="Intro 2" />
          </div>
          {/* 更多图片 */}
        </Slider>
      </div>
      <div className="intro__content">
        <Slider {...settings}>
          {/* 这里放置你的文本内容，每个内容作为一个slide */}
          <div>
            <h2>About me</h2>
            <p>I am currently studying computer science & data science at the University of Wisconsin–Madison</p>
          </div>
          <div>
            <h2>My Projects</h2>
            <p>Here is some information about my projects...</p>
          </div>
          {/* 更多文本内容 */}
        </Slider>
      </div>
    </div>
  );
};

export default Intro;
