import React, { useState } from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Intro.css';

const Intro = () => {
  const [currentKeyword, setCurrentKeyword] = useState(null);
  const keywords = [
    { word: "Beijing", title: "北京", description: "我生活在北京，这是一个美丽的城市。" },
    { word: "UWMadison", title: "UW-Madison", description: "我在UW-Madison学习计算机科学。" },
    { word: "CS", title: "计算机科学", description: "计算机科学是我的专业，我对它充满热情。" }
  ];

  // 假设这个函数会在指示器移动到关键词下方时被调用
  const handleKeywordHover = (keyword) => {
    setCurrentKeyword(keyword);
  };

  return (
    <div className="intro">
      <div className="intro__card">
        {currentKeyword ? (
          <>
            <h3>{currentKeyword.title}</h3>
            <p>{currentKeyword.description}</p>
          </>
        ) : (
          <p>请将指示器移动到关键词上查看详情。</p>
        )}
      </div>
      <div className="intro__text">
        <h2>About me</h2>
        <p>我生活在<span onMouseOver={() => handleKeywordHover(keywords[0])}>Beijing</span>，学习在<span onMouseOver={() => handleKeywordHover(keywords[1])}>UWMadison</span>，学习计算机<span onMouseOver={() => handleKeywordHover(keywords[2])}>CS</span>。</p>
        {/* 这里可以添加一个进度条或移动的点的动画 */}
      </div>
    </div>
  );
};

export default Intro;
