import React, { useEffect, useState } from 'react';
import GitHubIcon from '@material-ui/icons/GitHub';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import InsertDriveFileIcon from '@material-ui/icons/InsertDriveFile';
import { about } from '../../portfolio';
import { Button } from 'react-bootstrap';
import './About.css';

const About = () => {
  const { photo, name, resume, social } = about;
  const resumeFile = `${process.env.PUBLIC_URL}/Weibing_Wang_Resume.pdf`;

  const roles = ["软件工程师", "前端开发者", "数据科学家", "UI/UX 设计师"];
  const [currentRole, setCurrentRole] = useState("");
  const [roleIndex, setRoleIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => {
      setCurrentRole((prev) => roles[roleIndex].slice(0, charIndex));
      setCharIndex((prev) => prev + 1);

      if (charIndex === roles[roleIndex].length + 1) {
        setTimeout(() => {
          setRoleIndex((prev) => (prev + 1) % roles.length);
          setCharIndex(0);
        }, 2000);
      }
    }, 150);

    return () => clearTimeout(timer);
  }, [charIndex, roleIndex, roles]);

  return (
    <div className='about center'>
      <div className="about__content">
        <h1 className="about__hello">Hello</h1>
        <h2 className='about__name'>I'm {name}</h2>
        <h3 className='about__role'>{currentRole}</h3>

        <div className='about__contact center'>
          {resume && (
            <a href={resumeFile}>
              <Button className="mt-3 d-flex align-items-center link--icon">
                <InsertDriveFileIcon />
                Download Resume
              </Button>
            </a>
          )}
          {social && (
            <>
              {social.github && (
                <a href={social.github} aria-label='github' className='link link--icon'>
                  <GitHubIcon />
                </a>
              )}
              {social.linkedin && (
                <a href={social.linkedin} aria-label='linkedin' className='link link--icon'>
                  <LinkedInIcon />
                </a>
              )}
            </>
          )}
        </div>
      </div>
      {photo && (
        <img alt="Avatar placeholder" src={photo} className="about__photo" />
      )}
    </div>
  );
};

export default About;
