import GitHubIcon from '@material-ui/icons/GitHub';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import InsertDriveFileIcon from '@material-ui/icons/InsertDriveFile';
import { about } from '../../portfolio';
import { Button } from 'react-bootstrap';
import React, { useEffect, useRef } from 'react';
import './About.css';

const About = () => {
  const { photo, name, role, resume, social } = about;
  const resumeFile = `${process.env.PUBLIC_URL}/Weibing_Wang_Resume.pdf`;
  const helloRef = useRef(null);


  useEffect(() => {
    const helloElement = helloRef.current;
    const handleAnimationEnd = () => {
      helloElement.classList.add('done');
    };

    helloElement.addEventListener('animationend', handleAnimationEnd);

    return () => {
      helloElement.removeEventListener('animationend', handleAnimationEnd);
    };
  }, []);

  return (
    <div className='about center'>
    <div className="about__content">
      <h1 className="about__hello">Hello</h1>
      <h2 className='about__name'>I'm {name}</h2>
      <h3 className='about__role'>{role}.</h3>

        <div className='about__contact center'>
          {resume && (
            <a href={resume}>
              <span type='button' className='btn btn--outline'>
                Resume
              </span>
            </a>
          )}
          {social && (
            <>
              {social.github && (
                <a
                  href={social.github}
                  aria-label='github'
                  className='link link--icon'
                >
                  <GitHubIcon />
                </a>
              )}
              {social.linkedin && (
                <a
                  href={social.linkedin}
                  aria-label='linkedin'
                  className='link link--icon'
                >
                  <LinkedInIcon />
                </a>
              )}
              {resume && (
                <Button
                  href={resumeFile}
                  className="mt-3 d-flex align-items-center link--icon"
                >
                  <InsertDriveFileIcon />
                  Download Resume
                </Button>
              )}
            </>
          )}
        </div>
      </div>
      {photo && (
        <img alt="Avatar placeholder" src={photo} className="about__photo" />
      )}
    </div>
  );
};

export default About;
