import React, { useState, useEffect } from 'react';
import './Intro.css';

const Intro = () => {
  const [highlightedKeywordIndex, setHighlightedKeywordIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setHighlightedKeywordIndex(prevIndex => (prevIndex + 1) % 3);
    }, 3000); // 每3秒高亮下一个关键词

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="intro">
      <div className="intro__text">
        <h2>About Me</h2>
        <p>
          My journey began in <span className={`keyword ${highlightedKeywordIndex === 0 ? "highlighted" : ""}`}>Beijing</span>,
          continued at <span className={`keyword ${highlightedKeywordIndex === 1 ? "highlighted" : ""}`}>UW-Madison</span>,
          and is currently focused on <span className={`keyword ${highlightedKeywordIndex === 2 ? "highlighted" : ""}`}>CS & Math</span>.
        </p>
      </div>
    </div>
  );
};

export default Intro;
