import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Intro.css';

const Intro = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  return (
    <div className="intro">
      <div className="intro__image-container">
        <Slider {...settings}>
          {/* 更新图片路径 */}
          <div>
            <img src="src/components/Access/mePhoto2.jpg" alt="Intro" />
          </div>
          {/* 如果有更多图片，继续添加div和img标签 */}
        </Slider>
      </div>
      <div className="intro__content">
        <Slider {...settings}>
          {/* 这里放置你的文本内容，每个内容作为一个slide */}
          <div>
            <h2>About me</h2>
            <p>I am currently studying computer science & data science at the University of Wisconsin–Madison</p>
          </div>
          {/* 如果有更多文本内容，继续添加div */}
        </Slider>
      </div>
    </div>
  );
};

export default Intro;
